var command = ["svn"];

var args = request.props.getProperty("query.command");
command.push(args || "info");

var p = spawn(command);

var s = "";
if (request.props.getProperty("query.debug"))
    s += prettyPrintProplist(request.props);

s += "running " + command +  "
" + "exit value " + p.code + "
";
if (p.code !== 0) s+= p.stderr.join("
"); else  s+= p.stdout.join("
");

s;
